import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const EditSaleModal = () => {
  const { id } = useParams();
  const history = useHistory();
  const [sale, setSale] = useState(null);
  const [items, setItems] = useState([]);
  const [show, setShow] = useState(true);

  useEffect(() => {
    const fetchSale = async () => {
      try {
        const response = await axios.get(`/api/sales/${id}`);
        setSale(response.data);
        setItems(response.data.itemsSold);
      } catch (error) {
        console.error('Failed to fetch sale data:', error.response ? error.response.data : error);
      }
    };
    fetchSale();
  }, [id]);

  const handleClose = () => {
    setShow(false);
    history.push('/retail-tracking');
  };

  const handleSaveChanges = async () => {
    try {
      await axios.patch(`/api/sales/${id}`, { itemsSold: items });
      console.log('Sale updated successfully');
      handleClose();
    } catch (error) {
      console.error('Failed to update sale:', error.response ? error.response.data : error);
    }
  };

  const handleItemChange = (index, field, value) => {
    const updatedItems = [...items];
    updatedItems[index] = { ...updatedItems[index], [field]: value };
    setItems(updatedItems);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Edit Sale</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {items.map((item, index) => (
          <div key={index} className="edit-sale-item">
            <input
              type="text"
              value={item.itemName}
              readOnly
            />
            <input
              type="number"
              value={item.quantity}
              onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value))}
            />
            <input
              type="number"
              value={item.price}
              onChange={(e) => handleItemChange(index, 'price', parseFloat(e.target.value))}
            />
          </div>
        ))}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleSaveChanges}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditSaleModal;
