import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const EditSaleModal = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [sale, setSale] = useState(null);
  const [items, setItems] = useState([]);
  const [show, setShow] = useState(true);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSale = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/api/sales/${id}`);
        setSale(response.data);
        setItems(response.data.itemsSold);
        setLoading(false);
      } catch (error) {
        console.error('Failed to fetch sale data:', error.response ? error.response.data : error);
        setError('Failed to fetch sale data');
        setLoading(false);
      }
    };
    fetchSale();
  }, [id]);

  const handleClose = () => {
    setShow(false);
    navigate('/retail-tracking');
  };

  const handleSaveChanges = async () => {
    try {
      setLoading(true);
      await axios.patch(`/api/sales/${id}`, { itemsSold: items });
      console.log('Sale updated successfully');
      setLoading(false);
      handleClose();
    } catch (error) {
      console.error('Failed to update sale:', error.response ? error.response.data : error);
      setError('Failed to update sale');
      setLoading(false);
    }
  };

  const handleItemChange = (index, field, value) => {
    const updatedItems = [...items];
    updatedItems[index] = { ...updatedItems[index], [field]: value };
    setItems(updatedItems);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Edit Sale</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {items.map((item, index) => (
          <div key={index} className="edit-sale-item">
            <input
              type="text"
              value={item.itemName}
              readOnly
            />
            <input
              type="number"
              value={item.quantity}
              onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value))}
            />
            <input
              type="number"
              value={item.price}
              onChange={(e) => handleItemChange(index, 'price', parseFloat(e.target.value))}
            />
          </div>
        ))}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleSaveChanges}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditSaleModal;
