import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const navigate = useNavigate();
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.log("No token found, redirecting to login");
      navigate('/login');
      return;
    }

    axios.get('/api/user/role', {
        headers: {
            Authorization: `Bearer ${token}`
        }
    })
    .then((response) => {
      console.log("User role fetched successfully", response.data);
      setUserRole(response.data.role);
    })
    .catch((error) => {
      console.error("Failed to fetch user role:", error.response ? error.response : error);
      navigate('/login'); // Redirect to login if fetching role fails
    });
  }, [navigate]);

  const logoutHandler = () => {
    localStorage.removeItem('token'); // Assuming token is stored in localStorage
    console.log("User logged out.");
    navigate('/login'); // Redirect to login page after logout
  };

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>
      {userRole === 'boss' ? (
        <div>
          <button onClick={() => navigate('/retail-tracking')}>Retail Tracking</button>
          {/* Add more services for boss here */}
        </div>
      ) : (
        <div>
          <button onClick={() => navigate('/retail-tracking')}>Retail Tracking</button>
          {/* Services for employees can be added here */}
        </div>
      )}
      <button onClick={logoutHandler}>Logout</button>
    </div>
  );
};

export default Dashboard;
